mapper.select:
SELECT

FROM sys_dict_type

    and id=#{id}
    and `code`=#{code}
    and `name`=#{name}
    and is_sys=#{isSys}
    and remark=#{remark}
    and version=#{version}
    and create_by=#{createBy}
    and create_time=#{createTime}
    and update_by=#{updateBy}
    and update_time=#{updateTime}
    and is_delete=#{isDelete}
